import React, { useState, useEffect } from "react";
import { Button } from "../Button/Button";
import { Link } from "react-router-dom";
import "./Navbar.css";

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [navbar, setNavbar] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener("resize", showButton);

  const changeBackground = () => {
    if (window.scrollY >= 250) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };
  window.addEventListener("scroll", changeBackground);

  return (
    <>
      <nav className={navbar ? "navbar active" : "navbar"}>
        <div className="navbar-container">
          <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
            Archi<span>LUX</span>
          </Link>
          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? "close-menu" : "fas fa-bars"} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                HOME
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/services"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                SERVICES
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/projects"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                PROJECTS
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/about" className="nav-links" onClick={closeMobileMenu}>
                ABOUT
              </Link>
            </li>

            <li>
              <Link
                to="/contact"
                className="nav-links-mobile"
                onClick={closeMobileMenu}
              >
                Contact
              </Link>
            </li>
          </ul>
          <Link to="/contact" className="btn-mobile">
            {button && <Button buttonStyle="btn--outline">CONTACT US</Button>}
          </Link>
          {/* <div className="language">
            <select name="language">
              <option>FR</option>
              <option>EN</option>
            </select>
          </div> */}

          <li id="locales" class="item has-dropdown not-click">
            <a href="#">
              {" "}
              <img src="/images/flags/en-dd9e149e.png" /> EN
            </a>{" "}
            <ul class="dropdown">
              <li class="title back js-generated">
                <h5>
                  <a href="#">Back</a>
                </h5>
              </li>
              <li>
                <a href="/de/download_success.html">
                  {" "}
                  <img src="/images/flags/de-1daf2d67.png" /> DE
                </a>{" "}
              </li>
              <li>
                <a href="/download_success.html">
                  {" "}
                  <img src="/images/flags/en-dd9e149e.png" /> EN
                </a>{" "}
              </li>
              <li>
                <a href="/es/download_success.html">
                  {" "}
                  <img src="/images/flags/es-a88880bf.png" /> ES
                </a>{" "}
              </li>
              <li>
                <a href="/fr/download_success.html">
                  {" "}
                  <img src="/images/flags/fr-0f67f12d.png" /> FR
                </a>{" "}
              </li>
              <li>
                <a href="/hu/download_success.html">
                  {" "}
                  <img src="/images/flags/hu-9a189d12.png" /> HU
                </a>{" "}
              </li>
              <li>
                <a href="/it/download_success.html">
                  {" "}
                  <img src="/images/flags/it-c786c15b.png" /> IT
                </a>{" "}
              </li>
              <li>
                <a href="/jp/download_success.html">
                  {" "}
                  <img src="/images/flags/jp-3c5b1754.png" /> JP
                </a>{" "}
              </li>
              <li>
                <a href="/pl/download_success.html">
                  {" "}
                  <img src="/images/flags/pl-e383eba9.png" /> PL
                </a>{" "}
              </li>
              <li>
                <a href="/pt_br/download_success.html">
                  {" "}
                  <img src="/images/flags/pt_br-e39a2058.png" /> PT BR
                </a>{" "}
              </li>
              <li>
                <a href="/ro/download_success.html">
                  {" "}
                  <img src="/images/flags/ro-f141b663.png" /> RO
                </a>{" "}
              </li>
              <li>
                <a href="/ru/download_success.html">
                  {" "}
                  <img src="/images/flags/ru-24b172c5.png" /> RU
                </a>{" "}
              </li>
              <li>
                <a href="/tr/download_success.html">
                  {" "}
                  <img src="/images/flags/tr-e1821d04.png" /> TR
                </a>{" "}
              </li>
              <li>
                <a href="/ur/download_success.html">
                  {" "}
                  <img src="/images/flags/ur-952dd97a.png" /> UR
                </a>{" "}
              </li>
              <li>
                <a href="/zh_cn/download_success.html">
                  {" "}
                  <img src="/images/flags/zh_cn-9c7185e0.png" /> ZH CN
                </a>{" "}
              </li>
              <li>
                <a href="/zh_tw/download_success.html">
                  {" "}
                  <img src="/images/flags/zh_tw-92fe4f80.png" /> ZH TW
                </a>{" "}
              </li>
            </ul>
          </li>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
