import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import "./LangSelector.css";

function LangSelector() {
  const { i18n } = useTranslation();
  const [selectedLang, setSelectedLang] = useState("en");

  const changeLanguage = (event) => {
    setSelectedLang(event.target.value);
    i18n.changeLanguage(event.target.value);
  };

  const updateLog = () => {
    setSelectedLang(event.target.value);
    i18n.changeLanguage(event.target.value);
  };

  ".radio-group__option".change(updateLog);

  return (
    <>
      <div onChange={changeLanguage} className="language-container">
        <select name="language-select">
          <option
            type="select"
            value="en"
            name="language"
            defaultChecked={selectedLang === "en"}
          >
            EN
          </option>
          <option
            type="select"
            value="fr"
            name="language"
            defaultChecked={selectedLang === "fr"}
          >
            FR
          </option>
        </select>
      </div>

      <div class="wrap">
        <div class="radio-group" onChange={changeLanguage}>
          <input
            id="opt_1"
            class="radio-group__option"
            type="radio"
            name="language"
          />
          <label
            class="radio-group__label"
            for="opt_1"
            type="select"
            value="en"
            name="language"
            defaultChecked={selectedLang === "en"}
          >
            EN
          </label>

          <input
            id="opt_2"
            class="radio-group__option"
            type="radio"
            name="language"
          />
          <label
            class="radio-group__label"
            for="opt_2"
            type="select"
            value="fr"
            name="language"
            defaultChecked={selectedLang === "fr"}
          >
            FR
          </label>
        </div>
      </div>
    </>
  );
}

export default LangSelector;
