import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import "./LangSelector.css";

function LangSelector() {
  const { i18n } = useTranslation();
  const [selectedLang, setSelectedLang] = useState("en");

  const changeLanguage = (event) => {
    setSelectedLang(event.target.value);
    i18n.changeLanguage(event.target.value);
  };
  return (
    <div onChange={changeLanguage} className="language-container">
      <select name="language-select">
        <option
          type="select"
          value="en"
          name="language"
          defaultChecked={selectedLang === "en"}
        >
          EN
        </option>
        <option
          type="select"
          value="fr"
          name="language"
          defaultChecked={selectedLang === "fr"}
        >
          FR
        </option>
      </select>

      <p class="btn-switch">
        <input
          type="radio"
          id="yes"
          name="switch"
          class="btn-switch__radio btn-switch__radio_yes"
        />
        <input
          type="radio"
          checked
          id="no"
          name="switch"
          class="btn-switch__radio btn-switch__radio_no"
        />
        <label for="yes" class="btn-switch__label btn-switch__label_yes">
          <span class="btn-switch__txt">FR</span>
        </label>{" "}
        <label for="no" class="btn-switch__label btn-switch__label_no">
          <span class="btn-switch__txt">Non</span>
        </label>
      </p>
    </div>
  );
}

export default LangSelector;
